mapper.select:
SELECT T.ID ID, T3.HTBH JHBH, T2.LJMC LJMC, T2.LJTH LJTH, T1.GYDLMC GYDLMC, T.GYXLMC GYXLMC, T1.PX DPX, T.PX XPX, T.SL SL, T.SYSL SYSL FROM T_SCGL_LJGYBZ T, T_SCGL_GYDLBZ T1, T_SCJHGL_LJGL T2, T_SCJHGL_HTGL T3 WHERE 1=1 AND T.GYDLBZID = T1.ID AND T1.LJID = T2.ID AND T2.HTID = T3.ID
AND T3.ID = #{entity.jhbh}
AND T2.ID = #{entity.ljmc}
AND T1.ID = #{entity.gydlmc}
AND T.ID = #{entity.gyxlmc}
ORDER BY JHBH,LJMC,DPX,XPX
