mapper.select:
SELECT T3.ID JHID, T2.ID LJID, T2.LJMC FROM T_SCGL_LJGYBZ T, T_SCGL_GYDLBZ T1, T_SCJHGL_LJGL T2, T_SCJHGL_HTGL T3 WHERE 1=1 AND T.GYDLBZID = T1.ID AND T1.LJID = T2.ID AND T2.HTID = T3.ID
AND T2.HTID = #{jhid}
GROUP BY LJID

SELECT T1.* FROM T_SCJHGL_HTGL T, T_SCJHGL_LJGL T1 WHERE 1=1 AND T1.HTID = T.ID
AND T1.HTID = #{jhid}
ORDER BY T1.LJMC
