mapper.select:
select a.ygid, A.XM, h.sbmc, g.htbh jhbh, f.ljth, f.ljmc, e.gydlmc, d.gyxlmc, c.YWCL, a.bz from t_scgl_rcrwfp a, t_scgl_rcrwfp_rg_sb b, t_scgl_rcrwfp_rg_rw c, T_SCGL_LJGYBZ d, T_SCGL_GYDLBZ e, t_scjhgl_ljgl f, t_scjhgl_htgl g, t_sbgl_sbgl h where a.id = b.rcrwfpid AND b.id = c.fpsbid and h.id = b.sbid and c.ljgybzid = d.id and e.id = d.gydlbzid and e.ljid = f.id and g.id = f.htid and a.RQ = #{RQ}
AND a.ygID = #{ID}
order by a.xm,h.sbmc,g.htbh,f.ljmc,e.gydlmc,d.gyxlmc

SELECT T.ID, T.ZW, T.XM, T.RQ, T1.GSMC, T1.GS, T1.JB FROM T_SCGL_RCRWFP T, T_SCGL_RCRWFP_RG_GS T1 WHERE 1=1
AND T.ID = #{ID}
AND T.ID = T1.RCRWFPID AND T.RQ = #{RQ} ORDER BY T.ZW,T.XM
